import React, { useEffect, useState, useContext } from "react";
import { Button, Modal } from "semantic-ui-react";
import * as moment from "moment";
import "moment/locale/th";
import Axios from "axios";
import NavbarPage from "../components/navbarAdmin";
import {
  CBadge,
  CButton,
  CButtonGroup,
  CCard,
  CCardBody,
  CCardFooter,
  CCardHeader,
  CCol,
  CProgress,
  CRow,
  CCallout,
  CSelect,
} from "@coreui/react";
import CIcon from "@coreui/icons-react";
import { CChartLine } from "@coreui/react-chartjs";

import { Link, useHistory, useLocation } from "react-router-dom";
import { Form, Col } from "react-bootstrap";
import "./dashboard.css";

const Dashboard = () => {
  const [show, Setshow] = useState();
  const [showDropdown, SetshowDropdown] = useState(true);
  const [typeChart, settypeChart] = useState("Day");
  const [CategoryChart, setCategoryChart] = useState("จำนวนโพสต์");
  const [dataChart, setdataChart] = useState([]);
  const CategoriesChart = ["จำนวนโพสต์", "จำนวนผู้ใช้งานใหม่", "จำนวนค้นหา"];

  var dayOfWeek = [
    moment().subtract(6, "d").format("MMMM DD"),
    moment().subtract(5, "d").format("MMMM DD"),
    moment().subtract(4, "d").format("MMMM DD"),
    moment().subtract(3, "d").format("MMMM DD"),
    moment().subtract(2, "d").format("MMMM DD"),
    moment().subtract(1, "d").format("MMMM DD"),
    moment().format("MMMM DD"),
  ];
  var dayOfWeek = [
    moment().subtract(6, "d").format("MMMM DD"),
    moment().subtract(5, "d").format("MMMM DD"),
    moment().subtract(4, "d").format("MMMM DD"),
    moment().subtract(3, "d").format("MMMM DD"),
    moment().subtract(2, "d").format("MMMM DD"),
    moment().subtract(1, "d").format("MMMM DD"),
    moment().format("MMMM DD"),
  ];
  const ChangeCalender = (type) => {
    setCategoryChart(type);
    getData(type);
  };

  const onChangeTypeChart = (type) => {
    settypeChart(type);
    getData(type);
  };
  const countPost = () => {
    Axios.get("http://api").then((res) => {
      setdataChart(res.data);
    });
  };
  const countUser = () => {
    // Axios.get("http://localhost:7000/user/listuser").then((res) => {
    //   console.log(res.data)
    // });
  };
  const countSearch = () => {};
  const getData = (type, Calender) => {
    //sent type get data from api
    Axios.get("http://localhost:7000/user/listuser").then((res) => {
      console.log(res.data)
      // setdataChart([
      //   { label: "Data One", backgroundColor: "#33b5e5", data: res.countUser },
      // ]);
    });

    // setdataChart([
    //   {
    //     label: "Data One",
    //     backgroundColor: "#33b5e5",
    //     data: [30, 39, 10, 50, 30, 70, 35],
    //   },
    // ]);
  };

  useEffect(() => {
    //default get day
    getData(typeChart, CategoryChart);
  }, []);

  return (
    <div>
      <NavbarPage
        SetshowDropdown={SetshowDropdown}
        showDropdown={showDropdown}
      />
      <CCard>
        <CCardBody className="admin-cardbody">
          <CRow>
            <CCol sm="5">
              {/* <h4 id="traffic" className="card-title mb-0">
                จำนวน
              </h4>
              <div className="small text-muted">November 2017</div> */}
            </CCol>
            <CCol sm="7" className="d-none d-md-block">
              <CButtonGroup className="float-right mr-3">
                {/* 3 ปุ่ม */}
                {CategoriesChart.map((value) => (
                  <CButton
                    color="secondary"
                    className="admin-CategoriesChart"
                    onClick={() => onChangeTypeChart(value)}
                  >
                    {value}
                  </CButton>
                ))}
                {/* เลือก วันเดือนปี */}
                <CSelect
                  className="admin-select-chart"
                  onchange={(e) => ChangeCalender(e.target.value)}
                >
                  <option selected value="Day">
                    7 วันที่แล้ว
                  </option>
                  <option value="Month">30 วันที่แล้ว</option>
                  <option value="Year">รายปี</option>
                </CSelect>
              </CButtonGroup>
            </CCol>
          </CRow>
          {typeChart === "Day" ? (
            <CChartLine
              datasets={dataChart}
              options={{
                tooltips: {
                  enabled: true,
                },
              }}
              labels={dayOfWeek}
            />
          ) : null}
          {typeChart === "Month" ? (
            <CChartLine
              datasets={dataChart}
              options={{
                tooltips: {
                  enabled: true,
                },
              }}
              labels="months"
            />
          ) : null}
          {typeChart === "Month" ? (
            <CChartLine
              datasets={dataChart}
              options={{
                tooltips: {
                  enabled: true,
                },
              }}
              labels="months"
            />
          ) : null}
        </CCardBody>

        <CCardFooter>
          <CRow className="text-center">
            <CCol md sm="12" className="mb-sm-2 mb-0 d-md-down-none">
              <div className="text-muted">จำนวนโพสต์</div>
              <strong>24.093 โพสต์ </strong>
            </CCol>
            <CCol md sm="12" className="mb-sm-2 mb-0">
              <div className="text-muted">จำนวนผู้ใช้งานใหม่</div>
              <strong>78.706 คน </strong>
            </CCol>
            <CCol md sm="12" className="mb-sm-2 mb-0">
              <div className="text-muted">จำนวนค้นหา</div>
              <strong>22.123 ครั้ง </strong>
            </CCol>
          </CRow>
        </CCardFooter>
      </CCard>
    </div>
  );
};

export default Dashboard;
