                                  </Form.Group>
                                </Form.Row>

                                <Form.Row>
                                  <Form.Group
                                    as={Col}
                                    className="post-left col-lg-6 col-12"
                                    controlId="formGridId"
                                  >
                                    <Form.Label>
                                      เลขที่บัญชี (ผู้โกง)
                                    </Form.Label>
                                  </Form.Group>

                                  <Form.Group>
                                    <span className="spanpost">
                                      {res.accountnumber}
                                    </span>
                                  </Form.Group>
                                </Form.Row>

                                <Form.Row>
                                  <Form.Group
                                    as={Col}
                                    className="post-left col-lg-6 col-12"
                                    controlId="formGridNameproduct"
                                  >
                                    <Form.Label>ชื่อสินค้า</Form.Label>
                                  </Form.Group>

                                  <Form.Group>
                                    <span className="spanpost">
                                      {res.nameproduct}{" "}
                                    </span>
                                  </Form.Group>
                                </Form.Row>

                                <Form.Row>
                                  <Form.Group
                                    as={Col}
                                    className="post-left col-lg-6 col-12"
                                    controlId="formGridPrice"
                                  >
                                    <Form.Label>จำนวนเงิน</Form.Label>
                                  </Form.Group>

                                  <Form.Group>
                                    <span className="spanpost">
                                      {res.money.toLocaleString(undefined, {
                                        maximumFractionDigits: 2,
                                      })}{" "}
                                      บาท
                                    </span>
                                  </Form.Group>
                                </Form.Row>

                                <Form.Row>
                                  <Form.Group
                                    as={Col}
                                    className="post-left col-lg-6 col-12"
                                    controlId="formGridDate"
                                  >
                                    <Form.Label>วันที่โดนโกง</Form.Label>
                                  </Form.Group>

                                  <Form.Group>
                                    <span className="spanpost">
                                      {moment(
                                        new Date(res.datetimes.seconds * 1000)
                                      ).format("MM/DD/YYYY HH:mm")}{" "}
                                    </span>
                                  </Form.Group>
                                </Form.Row>

                                <Form.Row>
                                  <Form.Group
                                    as={Col}
                                    className="post-left col-lg-6 col-12"
                                    controlId="formGridDate"
                                  >
                                    <Form.Label>
                                      จำนวนครั้งที่ {res.name} {res.surname}{" "}
                                      ถูกแจ้ง{" "}
                                    </Form.Label>
                                  </Form.Group>
                                  <Form.Group>
                                    <span className="spanpost">
                                      {res.count} ครั้ง
                                    </span>
                                  </Form.Group>
                                </Form.Row>
                                <Form.Row>
                                  <Form.Group
                                    as={Col}
                                    className="post-left col-lg-6 col-12"
                                    controlId="formGridPrice"
                                  >
                                    <Form.Label>
                                      {" "}
                                      ยอดเงินรวมทั้งหมดที่โกงไป{" "}
                                    </Form.Label>
                                  </Form.Group>

                                  <Form.Group>
                                    <span className="spanpost">
                                      {res.summoney.toLocaleString(undefined, {
                                        maximumFractionDigits: 2,
                                      })}{" "}
                                      บาท
                                    </span>
                                  </Form.Group>
                                </Form.Row>
                              </Form>
                              <div className="postothers">
                                <Link
                                  className="postothers1"
                                  onClick={() => (
                                    history.push(`/mypost/${res.uid}`),
                                    window.location.reload(true)
                                  )}
                                >
                                  ดูเพิ่มเติม
                                </Link>
                              </div>
                            </div>

                            <div className="line-posts1"></div>
                          
                          </div>

                        </div>
                      </div>
                    );
                  })
                ) : null}
              </div>

                  <div className="column-post-right1">
                  <a href="https://www.facebook.com/MonkeyFruad-105444291586616">
              <div className="container-postright1">
                <div className="post-linkpost2">
                  ติดต่อเพจน้องพะโล้ <br />
                  เพื่ออัพเดทข่าวสารและพูดคุยกันได้ที่นี่
                </div>
                <div className="post-img1">
                  <img className="facebook" src="/img/facebooklogo.png" />
                </div>
              </div>
            </a>
                 </div>
            </div>
          </div>
        </div>
        <Chatbot />{" "}

        </div>
      ) : null}
    </div>
  );
};

