import React, { useEffect, useState } from "react";
import { Form, Col, FormControl, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import Axios from "axios";
import * as moment from "moment";
import "moment/locale/th";
const Non_listverifypost = ({ reportelement}) => {
  const [usernamePost, setUsernamePost] = useState("");
  const [usernameReport, setUsernameReport] = useState();
  const [selectOne, setSelectOne] = useState(false);
  const [selectTwo, setSelectTwo] = useState(false);
  const [selectThree, setSelectThree] = useState(false);
  const [description, setDescription] = useState(false);
  const initReport = async () => {
    try {
      InitOtherData()
      const usernamepost = await Axios.get(
        `http://localhost:7000/post/mypost/${reportelement.postid}`
      );
      setUsernamePost(usernamepost.data.item);
      const usernamereport = await Axios.get(
        `http://localhost:7000/user/session/${reportelement.userreport}`
      );
      setUsernameReport(usernamereport.data.item);
    } catch (err) {
      console.log(err);
    }
  };
  const InitOtherData = () =>{
    if(reportelement.selectOne === ""){
      setSelectOne(false)
    }
    if(reportelement.selectTwo === ""){
      setSelectOne(false)
    }
    if(reportelement.selectThree === ""){
      setSelectOne(false)
    }
    if(reportelement.description === ""){
      setDescription(false)
    }
  }
  const ChangeRead = async (e) => {
    e.preventDefault()
    console.log("KUYSUS")
    await Axios.post(
      `http://localhost:7000/post/report/changeread/${reportelement.uid}`
    );
  };
  useEffect(() => {
    initReport();
  }, []);
  return (
    <div>
      <div className="container-history1">
        <div className="container-history2">
          <Form className="formsize-history">
            <Form.Row>
              <Form.Group
                as={Col}
                className="้history-left col-lg-6 col-12"
                controlId="formGridName"
              >
                <Form.Label>
                  ผุ้แจ้งการรายงาน :{" "}
                  {usernameReport && usernameReport[0].username}{" "}
                </Form.Label>
              </Form.Group>
              { selectOne ? 
              <Form.Group controlId="formBasicCheckbox">
                <Form.Check type="checkbox" label="ข้อมูลไม่เหมาะสม"checked disabled />
              </Form.Group>
              :
              <Form.Group controlId="formBasicCheckbox">
              <Form.Check type="checkbox" label="ข้อมูลไม่เหมาะสม" disabled/>
            </Form.Group>
              }
            </Form.Row>

            <Form.Row>
              <Form.Group
                as={Col}
                className="้history-left col-lg-6 col-12"
                controlId="formGridName"
              >
                <Form.Label>
                  วันเวลาที่แจ้ง :{" "}
                  {moment(new Date(reportelement.date.seconds * 1000)).format(
                    "MM/DD/YYYY HH:mm"
                  )}{" "}
                </Form.Label>
              </Form.Group>
              { selectTwo ? 
               <Form.Group controlId="formBasicCheckbox">
               <Form.Check type="checkbox" label="ข้อมูลไม่ถูกต้อง" checked disabled />
             </Form.Group>
              :
              <Form.Group controlId="formBasicCheckbox">
              <Form.Check type="checkbox" label="ข้อมูลไม่ถูกต้อง" disabled />
            </Form.Group>
              }
            </Form.Row>

            <Form.Row>
              <Form.Group
                as={Col}
                className="้history-left col-lg-6 col-12"
                controlId="formGridName"
              >
                <Form.Label>
                  เจ้าของโพสต์ : {usernamePost && usernamePost[0].username}{" "}
                </Form.Label>
              </Form.Group>

              <Form.Group controlId="formBasicCheckbox">
                <Form.Check type="checkbox" label="อื่นๆ" />
              </Form.Group>
            </Form.Row>

            <Form.Row>
              <Form.Group
                as={Col}
                className="้history-left col-lg-6 col-12"
                controlId="formGridName"
              >
                <Form.Label>เอกสารที่แนบมา : </Form.Label>
                <Form.Label>
                  จำนวนครั้งที่มีการรายงานโพสต์นี้ {reportelement.count} ครั้ง
                </Form.Label>
              </Form.Group>

              <Form.Group controlId="exampleForm.ControlTextarea1">
                <Form.Label>รายละเอียดเพิ่มเติม</Form.Label>
                <Form.Control as="textarea" rows={4} readOnly={true} />
              </Form.Group>
            </Form.Row>
          </Form>
          <div  onClick={(e)=>ChangeRead(e)} className="historyother">
            <Link
              className="historyother1"
              to={`/post/${reportelement.postid}`}
            >
             ตรวจสอบโพสต์
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Non_listverifypost;
