import React, { useEffect, useState, useContext } from "react";
import { Form, Col, FormControl, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import Axios from "axios";
import usercontext from "../context/usercontext";

const  Listcomment = ( {commentid }) => {
    const [isActive, setIsActive] = useState(false);
    const onClick = () => setIsActive(!isActive);
    let { user, setUser } = useContext(usercontext);
    const [item , Setitem] = useState([])
    const [checkedittext , Setcheckedittext] = useState(false)
    // const [edittextcomment , Setedittextcomment] = useState()
    const [textcomment , Settextcomment] = useState()
    const [edittextcomment , Setedittextcomment] = useState()
    const [data, Setdata] = useState();
    const [show, Setshow] = useState();
    const [userinfomation, Setuserinfomation] = useState();
    const [photo,  Setphoto] = useState();

      
  
    const deleted = async (commentid) => {
        const postdelete = await Axios.post(`http://localhost:7000/post/delete/comment/${commentid}`);
        window.location.reload(false);
    };
    const edit = async () =>{
      Setcheckedittext(true)
    }
    const handleedit = async (commentid) =>{
    const editcomment = await Axios.post(`http://localhost:7000/post/edit/comment/${commentid}`,  {edittextcomment} )
    
    }



    const handlecomment = async (uid) =>{
        try{
        
          console.log(uid)
          let sentdata = {textcomment , username : data[0].username , userid : user.uid , photoURL : photo}
          
          const sentcomment = await Axios.post(`http://localhost:7000/post/comment/${uid}`, sentdata)
          // const getcomment = await Axios.get(`http://localhost:7000/post/comment/${uid}`)
          // Setallcomment(getcomment.data.item)
          
        }catch(err){
          console.log(err)
        }
      }


      const gg = async () => {
        try {
            // ดึงข้อมูลเก่ามาใส้ใน input ก่อนแก้ไข
          Setedittextcomment(commentid.textcomment)
         
    
        } catch (err) {
          console.log(err);
        }
    
      };
      useEffect(() => {
        gg();
      }, []);
  

    return (
        <div>
            
               <div className="row mypostcommentrow">
         <div className="column1 mypostcommentrow1">
        <div class="vl"></div>
        <div className="mypost-comment-img1">
        <div className="post-profilecomment-img1">
                  {commentid.photoURL ? <img className="img-circle" src={`${commentid.photoURL.url}`}  /> : <img className="img-circle" src="/img/profile.png" /> }
             
                </div>
          <div className="mypost-comment-name1">
           {commentid ? "@" : null}{commentid.username}
            <span className="mypost-comment-time1"> {commentid.datetime} </span>
          </div>
          <br />
         {checkedittext ? <div><input value={edittextcomment} onChange={(e) =>{Setedittextcomment(e.target.value)}}></input> <button onClick={() => handleedit(commentid.commentid)}>ตกลง</button> </div>: <div className="mypost-comment-comments1">
            <div className="mypostcomment1">{commentid.textcomment}</div> 
          </div>  
         } 
        </div> 
       </div> 
       {user && commentid.userid == user.uid ? <div className="column2 mypostcommentrow2">
        <div className="menu-containermypostcommentsetting">
          <div onClick={onClick} className="mypostcommentbuttonsetting">
            <img 
              className="mypostcommentimg-setting"
              src="/img/setting.png"
              alt="avatar"
            ></img> 
       </div>

          <div
            className={`mypostcommentmenusetting ${
              isActive ? "active" : "inactive"
            }`}
          >
            <ul className="ul-mypostcommentmenusetting">
           <li className="li-mypostcommentmenusetting">
                <a className="a-mypostcommentmenusetting"
                onClick={() => edit(commentid.commentid)}
                >
                    แก้ไขคอมเมนต์
                </a>
              </li> 
              <li className="li-mypostcommentmenusetting">
                <a
                  className="a-mypostcommentmenusetting"
                  onClick={() => deleted(commentid.commentid)}
                >
                  {" "}
                  ลบคอมเมนต์{" "}
                </a>
              </li> 
            </ul>
          </div>
        </div>
      </div> : null }
    </div>



     {/* <button Onclick={() => handlemorecomment(res.uid)} className="postother2">ดูอีก 3 ความคิดเห็น</button> */}
     <div className="row post-comment-comments1">
                <div className="post-profilecomment-img1">
                  {commentid.photoURL ? <img className="img-circle" src={`${commentid.photoURL.url}`}  /> : <img className="img-circle" src="/img/profile.png" /> }
             
                </div>
                <div className="row post-comment-commentsall">
                  <div
                    className="post-writecommemt col-lg-6 col-10"
                    controlId="exampleForm.ControlTextarea1"
                  >
                     <input className="inputcomment" placeholder="เขียนความคิดเห็น..." value={textcomment} onChange={(e) =>{Settextcomment(e.target.value)}}/>
                  </div>

                          <div>
                            <div className="column2 mypostbuttonsend">
                              <button className="mypostbuttonsends" onClick={() => handlecomment(commentid.uid)}>
                                <i className="fa fa-paper-plane"></i>
                              </button>
                            </div>
                       
                          </div>
                </div>
                
              </div>



</div>

    )
}
export default Listcomment
