import React, {useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./chatbot.css";

import { useWindowScroll } from "react-use";

const Chatbot = () => {
  const [state, setState] = useState(false);
  const toggleHandler = () => {
    const newData = !state;
    setState(newData);


    /*scrool*/
    const { y: pageYOffset } = useWindowScroll();
    const [visible, setVisiblity] = useState(false);

    useEffect(() => {
        if (pageYOffset > 400) {
            setVisiblity(true);
        } else {
            setVisiblity(false);
        }
    }, [pageYOffset]);

    const scrollToTop = () => window.scrollTo({ top: 0, behavior: "smooth" });

    if (!visible) {
        return false;
  };
}
  return (
    

    <div className="foot">

<div className="scroll-to-top cursor-pointer text-center" onClick={scrollToTop}><i className="icon fas fa-chevron-up"></i></div>
      <div className="card-chatbot">
        <div className="card-chatbot-header text-left" onClick={toggleHandler}>
          <span className="text-card-header">Add Friend</span>
          <span>
            <i className="fas fa-minus"></i>
          </span>
        </div>
        {state ? (
          <div className="card-body-chatbot">
            <div>
              <img src="/img/paloqr.jpg" className="image-palo" />
              <a href="https://lin.ee/QlA8OaI" className="textlink-chatbot">
                คลิกเพื่อเพิ่มเพื่อนน้องพะโล้ <br/>เพื่อการค้นหาที่ไวขึ้น
              </a>
            </div>
          </div>
        ) : (
          <div></div>
        )}
      </div>
    </div>
  );
};
export default Chatbot;
