                                        )}
                                      >
                                        ดูเพิ่มเติม
                                      </Link>
                                    </div>
                                  </div>

                                  <div className="line-posts1"></div>
                                  <div className="container-posts4">
                                    <Commentitem postid={res.uid} />
                                  </div>
                                </div>
                              </div>
                            );
                          })
                        ) : (
                          <div></div>
                        )}
                      </div>
                    )}
                  </div>
                )}
              </div>
            )}
            <div className="container-bottoms"></div>
          </div>
        </div>
      </div>
      <Chatbot />
    </div>
  );
};

export default Post;
