import React, { useEffect, useState, useContext } from "react";
import { Form, Col, FormControl, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import Axios from "axios";
import "./commentitem.css";
import usercontext from "../context/usercontext";
import Listcomment from "./listcomment"
import _ from "lodash"



const Commentitem = ({   postid  }) => {

  const [isActive, setIsActive] = useState(false);
  const onClick = () => setIsActive(!isActive);
  let { user, setUser } = useContext(usercontext);
  const [comment , Setcomment] = useState()
  const [commentmore , Setcommentmore] = useState()
  const [item , Setitem] = useState([])
  const [checkedittext , Setcheckedittext] = useState(false)
  // const [edittextcomment , Setedittextcomment] = useState()
  const [textcomment , Settextcomment] = useState()

    

  const deleted = async (commentid) => {
      const postdelete = await Axios.post(`http://localhost:7000/post/delete/comment/${commentid}`);
      window.location.reload(false);
  };

 
   const handlemorecomment = async () =>{
    try{
      console.log("ok")
      const getcomment = await Axios.get(`http://localhost:7000/post/commentmore/${postid}` )
     
      Setcommentmore(getcomment.data.item)

    }catch(err){
      console.log(err)
    }
  }
 

  const gg = async () => {
    try {
      const getcomment = await Axios.get(`http://localhost:7000/post/comment/${postid}` )
      Setcomment(getcomment.data.item)
    } catch (err) {
      console.log(err);
    }

  };
  useEffect(() => {
    gg();
  }, []);
  

  
  return (
      <div>
      {commentmore ? commentmore.map(commentmore =>{

          return (
                 <Listcomment  commentmore={commentmore} /> 
            
          )
      }): <div>{comment ? comment.map(comment =>{

        return (
               <Listcomment  comment={comment}  /> 
          
        )
    }): null} </div>}

<button onClick={handlemorecomment} className="postother2">ดูอีก 3 ความคิดเห็น</button>
      
    </div> 


  
  );
};

export default Commentitem;
