  const [refresh,   Setrefresh] = useState();

  const [haha,   Sethaha] = useState();
  const [error,   Seterror] = useState();
 
  let history = useHistory()

  const logout = () => {
    auth
      .signOut()
      .then(() => {
        console.log("Signout");
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const toggleCollapse = () => {
    setIsopen(!isOpen);
  };


