import React, { useEffect, useState, useContext } from "react";
import { Link, useParams, useHistory } from "react-router-dom";
import { Dropdown, DropdownButton } from "react-bootstrap";
import { Form, Col, FormControl, Button } from "react-bootstrap";
import {
  auth,
  googleProvider,
  facebookProvider,
  firestore,
} from "../Frontfirebase";
import Axios from "axios";
import NavbarPage from "../components/navnew";
// import Commentitem from "../components/commentitem";
import Commentitemformypost from "../components/commentitemformypost";
import "./mypost.css";

import usercontext from "../context/usercontext";
const Mypost = () => {
  const [isActive, setIsActive] = useState(false);
  const onClick = () => setIsActive(!isActive);


  const [mypost, Setmypost] = useState();


  let { user, setUser } = useContext(usercontext);

  let { uid } = useParams();
  const history = useHistory();

  const deleted = async (uid) => {
    const postdelete = await Axios.post(
      `http://localhost:7000/post/delete/${uid}`
    );

    history.push("/post/history");
  };


  const ok = async () => {
    try {
      const ok = await Axios.get(`http://localhost:7000/post/mypost/${uid}`);
      Setmypost(ok.data.item);
      console.log("555")
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    ok();
  }, []);
  
  return (
    <div className="allpage">
      {mypost ? <div> <NavbarPage />
      <h1 className="h1-mypost">โพสต์ของฉัน</h1>
      {mypost
        ? show.map((res) => {

            return (
              <div>
                
                <div className="container-post2">
                  <div className="cotainer-post3">
                    <div className="post-profile-img">
                      {res.photoURL ? (
                        <img
                          className="img-circle"
                          src={`${res.photoURL.url}`}
                        />
                      ) : (
                        <img className="img-circle" src={"/img/profile.png"} />
                      )}
                      <div className="post-name">
                        {res.username ? "@" : null}
                        {res.username}
                      </div>
                      <br />
                      <div className="post-date">
                        <span className="post-time">{res.date}</span>
                      </div>
                    </div>

                    <div className="postbuttonreport">
                      <a className="postbuttonreported" href="/post/edit">
                        <i class="fa fa-flag"></i>
                      </a>
                    </div>

                    <div className="container-post4">
                      <div className="container-post5">
                        <Form className="formsize-post">
                          <Form.Row>
                            <Form.Group
                              as={Col}
                              className="้post-left col-lg-6 col-12"
                              controlId="formGridName"
                            >
                              <Form.Label>ชื่อ - นามสกุลผู้โกง</Form.Label>
                            </Form.Group>

                            <Form.Group>
                              <span className="spanpost">
                                {res.name} {res.surname}
                              </span>
                            </Form.Group>
                          </Form.Row>

                          <Form.Row>
                            <Form.Group
                              as={Col}
                              className="post-left col-lg-6 col-12"
                              controlId="formGridId"
                            >
                              <Form.Label>เลขที่บัญชี (ผู้โกง)</Form.Label>
                            </Form.Group>

                            <Form.Group>
                              <span className="spanpost">
                                {res.accountnumber}
                              </span>
                            </Form.Group>
                          </Form.Row>

                          <Form.Row>
                            <Form.Group
                              as={Col}
                              className="post-left col-lg-6 col-12"
                              controlId="formGridNameproduct"
                            >
                              <Form.Label>ชื่อสินค้า</Form.Label>
                            </Form.Group>

                            <Form.Group>
                              <span className="spanpost">
                                {res.nameproduct}{" "}
                              </span>
                            </Form.Group>
                          </Form.Row>

                          <Form.Row>
                            <Form.Group
                              as={Col}
                              className="post-left col-lg-6 col-12"
                              controlId="formGridPrice"
                            >
                              <Form.Label>จำนวนเงิน</Form.Label>
                            </Form.Group>

                            <Form.Group>
                              <span className="spanpost">{res.money} บาท</span>
                            </Form.Group>
                          </Form.Row>

                          <Form.Row>
                            <Form.Group
                              as={Col}
                              className="post-left col-lg-6 col-12"
                              controlId="formGridDate"
                            >
                              <Form.Label>วันที่โพสต์</Form.Label>
                            </Form.Group>

                            <Form.Group>
                              <span className="spanpost">{res.date} </span>
                            </Form.Group>
                          </Form.Row>

                          <Form.Row>
                            <Form.Group
                              as={Col}
                              className="post-left col-lg-6 col-12"
                              controlId="formGridDate"
                            >
                              <Form.Label>
                                จำนวนครั้งที่ {res.name} {res.surname} ถูกแจ้ง{" "}
                              </Form.Label>
                            </Form.Group>
                            <Form.Group>
                              <span className="spanpost">
                                {res.count} ครั้ง
                              </span>
                            </Form.Group>
                          </Form.Row>
                          <Form.Row>
                            <Form.Group
                              as={Col}
                              className="post-left col-lg-6 col-12"
                              controlId="formGridPrice"
                            >
                              <Form.Label>
                                {" "}
                                ยอดเงินรวมทั้งหมดที่โกงไป{" "}
                              </Form.Label>
                            </Form.Group>

                            <Form.Group>
                              <span className="spanpost">
                                {res.summoney} บาท
                              </span>
                            </Form.Group>
                          </Form.Row>
                        </Form>
                        <div className="postother">
                          <Link
                            className="postother1"
                            onClick={() => (history.push(`/mypost/${res.uid}`),window.location.reload(true))}
                          >
                            ดูเพิ่มเติม
                          </Link>
                        </div>
                      </div>

                      <div className="line-post1"></div>
                      <div className="container-post6">
                     
                      </div>
                      <Commentitem postid={res.uid} />
                      <Chatbot />
                    </div>
                  </div>
                </div>
                <div className="column-post-right"></div>
              </div>
            );
          })
        : null} </div>:null}
     
    </div>
  );
};
export default Mypost;
