                  </Link>
                </div>
            
              <div className="line-post1"></div>
              <div className="container-post6">
          
                  <Commentitem   postid={res.uid} commentmore={commentmore} onclick={onclick}/>;
                
             
                {/* <div className="line-comment2"></div> */}
              </div>
              <button onClick={() =>handlemorecomment(res.uid)} className="postother2">ดูอีก 3 ความคิดเห็น</button>
