import React, { useEffect, useState } from "react";
import NavbarPage from "../../user/components/navnew";
import "../../user/pages/history.css";
import ListNonverifypost from "../components/list_non_verifypost";
import Listverifypost from "../components/list_verify_post";
import Listhidereport from "../components/listhidereport";
import Axios from "axios";
const Report = () => {
  const [verifypost, setverifypost] = useState();
  const [nonverifypost, setNonverifypost] = useState();
  const [hidereport, setHidereport] = useState();
  const [showDropdown, SetshowDropdown] = useState(true);
  const [hideReport, sethideReport] = useState();
  const Hiddendropdown = () => {
    SetshowDropdown(false);
  };
  const initReport = async () => {
    try {
      const Allreport = await Axios.get(
        "http://localhost:7000/post/report/non_verify"
      );
      setNonverifypost(Allreport.data.report);
      console.log(Allreport.data.report);
    } catch (err) {
      console.log("error");
    }
  };
  const ActiveVerify = async (e) => {
    e.preventDefault()
    try {
      const Allreport = await Axios.get("http://localhost:7000/post/report/verify");
      setNonverifypost()
      setHidereport()
    } catch (err) {
      console.log(err);
    }
  };
  const ActiveNonVerify = async (e) => {
    e.preventDefault()
    try {
      const Allreport = await Axios.get(
        "http://localhost:7000/post/report/non_verify"
      );
      setNonverifypost(Allreport.data.report);
      setverifypost()
      setHidereport()
    } catch (err) {
      console.log("error");
    }
  };
  const Activehide = async (e) => {
    console.log("OK")
    e.preventDefault()
    try {
      const Allreport = await Axios.get(
        "http://localhost:7000/post/report/hide"
      );
      setHidereport(Allreport.data.report);
      setverifypost()
      setNonverifypost()
    } catch (err) {
      console.log("error");
    }
  };
  const hideClick = () =>{
    sethideReport(true)
  }
  useEffect(() => {
    initReport();
  }, [hideReport]);

  return (
    <div onClick={() => Hiddendropdown()}>
      <NavbarPage
        SetshowDropdown={SetshowDropdown}
        showDropdown={showDropdown}
      />
      <h1 className="h1-contractus">รายงานโพสต์</h1>
      <div className="container-history5">
      <div className="" onClick={(e)=>ActiveVerify(e)}>ตรวจสอบแล้ว</div>
      <div className="" onClick={(e)=>ActiveNonVerify(e)}>ยังไม่ตรวจสอบ</div>
      <div className="" onClick={(e)=>Activehide(e)}>ซ่อน</div>
      </div>
      {nonverifypost
        ? nonverifypost.map((reportelement, index) => {
            return (
              <ListNonverifypost reportelement={reportelement} key={index} />
            );
          })
        : null}
         {verifypost
        ? verifypost.map((reportelement, index) => {
            return (
              <Listverifypost reportelement={reportelement} key={index} hideClick={hideClick}/>
            );
          })
        : null}
         {hidereport
        ? hidereport.map((reportelement, index) => {
            return (
              <Listhidereport reportelement={reportelement} key={index} />
            );
          })
        : null}
    </div>
  );
};

export default Report;
