import React, { useEffect, useState , useContext  } from "react";
import NavbarPage from "../../user/components/navnew";
import listverifypost from "../components/list_verify_port";
import usercontext from "../context/usercontext";
import Axios from "axios";

const { v4: uuidv4 } = require("uuid");
const Verifypost = () => {

  const [mypost, Setmypost] = useState();
  const [click, Setclick] = useState();
  const [showDropdown, SetshowDropdown] = useState(true);
  let { user} = useContext(usercontext);
  let uuid = uuidv4();
  const Hiddendropdown = () => {
    SetshowDropdown(false);
  };
  const ok = async () => {
    try {
      const ok = await Axios.post("http://localhost:7000/post/postapi", {
        result: user,
      });
      Setmypost(ok.data.item);
    } catch (err) {
      console.log("error");
    }
  };
  const handledeletetorerender = async () => {
    Setclick(uuid);
  };
  useEffect(() => {
    ok();
  }, [click]);
  console.log(mypost);

  return (
    <div onClick={() => Hiddendropdown()}>
      <NavbarPage
        SetshowDropdown={SetshowDropdown}
        showDropdown={showDropdown}
      />
      <h1 className="h1-history">ประวัติการโพสต์</h1>
      <div className="container-history5">
        {mypost ? (
          <h2 className="h2-history">ทั้งหมด {mypost.length} โพสต์</h2>
        ) : null}
      </div>
      {mypost
        ? mypost.map((ok, index) => {
            return (
              <Historyitem
                ok={ok}
                user={user}
                key={index}
                handledeletetorerender={handledeletetorerender}
              />
            );
          })
        : null}
    </div>
  );
};

export default Verifypost;
