  facebookProvider,
  firestore,
} from "../Frontfirebase";
import { object } from "yup/lib/locale";
import usercontext from "../context/usercontext";
const { v4: uuidv4, NIL } = require("uuid");
const Post = () => {
 
  const [show, Setshow] = useState();

  let { user, setUser } = useContext(usercontext);

  const [isActive, setIsActive] = useState(false);
  const onClick = () => setIsActive(!isActive);
  let history = useHistory()
  


  const ok = async () => {
   
    const getpost = await Axios.get(`http://localhost:7000/post/post`);
    Setshow(getpost.data.item);

  };
  useEffect(() => {
    ok();
  }, []);

  return (
    <div>
      <NavbarPage />
      <div className="container-post1">
        <div className="row postrow">
          <div className="column1-postrow1">
            <div className="post-img">
              <img className="monkey" src="/img/logo v3.png" />
            </div>
          </div>
          <div className="column2-postrow2">
            <div className="post-linkformpost1">
              แจ้งข้อมูลคนโกงได้ที่นี่เลย
            </div>
            <br />
            <div className="post-linkformpost2">
              <Link to={`/linkruleshow/`}>
                <button className="buttonpost" type="submit">
                  คลิก
                </button>
              </Link>
            </div>
          </div>
        </div>
      </div>
      <h1 className="h1-post"> มีโพสทั้งหมด {show ? show.length : null} โพส</h1>

      {show
        ?  show.map((ok) => {
            return (
              <div>
                <div className="container-mypost">
                  <div className="cotainer-mypost2">
                    <div className="mypost-profile-img">
                      {ok.photoURL ? (
                        <img
                          className="img-circle"
                          src={`${ok.photoURL.url}`}
                        />
                      ) : (
                        <img className="img-circle" src="/img/profile.png" />
                      )}

                      <div className="mypost-name">
                        {ok.username ? "@" : null}
                        {ok ? ok.username : null}
                      </div>
                      <br />
                      <div className="mypost-date">
                        {ok.date}
                        {/* <span className="mypost-time">23:38 </span> */}
                      </div>
                    </div>

                   
                    <div className="container-mypost3">
                      <div className="mypostprofile-bad-img">
                        {ok.resultfile ? (
                          <img
                            className="img-circle"
                            src={`${ok.resultfile.url}`}
                          />
                        ) : (
                          <img className="img-circle" src="/img/profile.png" />
                        )}
                      </div>
                      <Form className="formsize-mypost">
                        <Form.Row>
                          <Form.Group
                            as={Col}
                            className="mypost-left col-lg-6 col-sm-6 col-12"
                            controlId="formGridName"
                          >
                            <Form.Label className="text-mypost">
                              ชื่อ (ผู้โกง){" "}
                              <span className="spanmypost">{ok.name}</span>
                            </Form.Label>
                          </Form.Group>

                          <Form.Group as={Col} controlId="formGridLastname">
                            <Form.Label className="text-mypost">
                              นามสกุล (ผู้โกง){" "}
                              <span className="spanmypost">{ok.surname}</span>
                            </Form.Label>
                          </Form.Group>
                        </Form.Row>

                        <Form.Row>
                          <Form.Group
                            as={Col}
                            className="mypost-left col-lg-6 col-sm-6 col-12"
                            controlId="formGridId"
                          >
                            <Form.Label className="text-mypost">
                              เลขบัตรประชาชน (ผู้โกง){" "}
                              <span className="spanmypost">{ok.id}</span>
                            </Form.Label>
                          </Form.Group>

                          <Form.Group
                            as={Col}
                            controlId="formGridAccountnumber"
                          >
                            <Form.Label className="text-mypost">
                              เลขที่บัญชี (ผู้โกง){" "}
                              <span className="spanmypost">
                                {ok.accountnumber}
                              </span>
                            </Form.Label>
                          </Form.Group>
                        </Form.Row>

                        <Form.Row>
                          <Form.Group
                            as={Col}
                            className="mypost-left col-lg-6 col-sm-6 col-12"
                            controlId="formGridNameproduct"
                          >
                            <Form.Label className="text-mypost">
                              ชื่อสินค้า{" "}
                              <span className="spanmypost">
                                {ok.nameproduct}
                              </span>
                            </Form.Label>
                          </Form.Group>

                          <Form.Group as={Col} controlId="formGridCategory">
                            <Form.Label className="text-mypost">
                              หมวดหมู่สินค้า{" "}
                              <span className="spanmypost">
                                {ok.productcategory}
                              </span>
                            </Form.Label>
                          </Form.Group>
                        </Form.Row>

                        <Form.Row>
                          <Form.Group
                            as={Col}
                            className="mypost-left col-lg-6 col-sm-6 col-12"
                            controlId="formGridPrice"
                          >
                            <Form.Label className="text-mypost">
                              จำนวนเงิน (บาท){" "}
                              <span className="spanmypost">{ok.money}</span>
                            </Form.Label>
                          </Form.Group>

                          <Form.Group as={Col} controlId="formGridCategory">
                            <Form.Label className="text-mypost">
                              ธนาคาร{" "}
                              <span className="spanmypost">{ok.bank}</span>
                            </Form.Label>
                          </Form.Group>
                        </Form.Row>

                        <Form.Row>
                          <Form.Group
                            as={Col}
                            className="mypost-left col-lg-6 col-sm-6 col-12"
                            controlId="formGridDate"
                          >
                            <Form.Label className="text-mypost">
                              วันที่โดนโกง{" "}
                              <span className="spanmypost">{ok.datetime}</span>
                            </Form.Label>
                          </Form.Group>

                          <Form.Group as={Col} controlId="formGridSocial">
                            <Form.Label className="text-mypost">
                              ช่องทางที่โดนโกง{" "}
                              <span className="spanmypost">{ok.social}</span>
                            </Form.Label>
                          </Form.Group>
                        </Form.Row>

                        <Form.Row>
                          <Form.Group as={Col} controlId="formGridSocial">
                            <Form.Label className="text-mypost">
                              จำนวนครั้งที่ {ok.name} {ok.surname} ถูกแจ้ง {" "}
                              <span className="spanmypost">
                                 {ok.count} ครั้ง
                              </span>
                            </Form.Label>
                          </Form.Group>
                          </Form.Row>
                          <Form.Row>
                          <Form.Group as={Col} controlId="formGridSocial">
                            <Form.Label className="text-mypost">
                              ยอดเงินรวมทั้งหมดที่โกงไป {" "}
                              <span className="spanmypost">
                                {ok.summoney} บาท
                              </span>
                            </Form.Label>
                          </Form.Group>
                        </Form.Row>

                        <Form.Group controlId="exampleForm.ControlTextarea1">
                          <Form.Label className="text-mypost">
                            รายละเอียดเพิ่มเติม{" "}
                            <span className="spanmypost">{ok.other}</span>
                          </Form.Label>
                        </Form.Group>
                        <div className="img-holder-badslip">
                          {ok.item
                            ? ok.item.map((res) => {
                                return (
                                  <img
                                    className="img-bad"
                                    alt=""
                                    src={`${res.url}`}
                                    style={{ overflow: "hidden" }}
                                    onMouseOver={(e) =>
                                      (e.currentTarget.style = {
                                        transform: "scale(1.25)",
                                        overflow: "hidden",
                                      })
                                    }
                                    onMouseOut={(e) =>
                                      (e.currentTarget.style = {
                                        transform: "scale(1)",
                                        overflow: "hidden",
                                      })
                                    }
                                  />
                                );
                              })
                            : null}
                        </div>
                      </Form>
                      <div className="line-comment1"></div>
                      <div className="container-mypost4">
                        <Commentitem postid={ok.uid} />
                      </div>
                 
                    </div>
                  </div>
                </div>
              </div>
            );
          })
        : null}
    </div>
  );
};

export default Post;
