import React, { useEffect, useState, useContext } from "react";
import { Button, Modal } from "semantic-ui-react";
import * as moment from "moment";
import "moment/locale/th";
import Axios from "axios";
import NavbarPage from "../components/navbarAdmin";
import {
  CBadge,
  CButton,
  CButtonGroup,
  CCard,
  CCardBody,
  CCardFooter,
  CCardHeader,
  CCol,
  CProgress,
  CRow,
  CCallout,
  CSelect,
} from "@coreui/react";
import CIcon from "@coreui/icons-react";
import { CChartLine } from "@coreui/react-chartjs";

import { Link, useHistory, useLocation } from "react-router-dom";

import { Form, Col } from "react-bootstrap";
import "./dashboard.css";

const Dashboard = () => {
  const [show, Setshow] = useState();
  const [showDropdown, SetshowDropdown] = useState(true);
  const [typeChart, settypeChart] = useState("Day");

  const [dataChart, setdataChart] = useState([]);

  const CategoriesChart = ["จำนวนผู้ใช้งานใหม่","จำนวนโพสต์","จำนวนค้นหา"]
  const onChangeTypeChart = (type) => {
    settypeChart(type);
    getData(type);

    setdataChart([
      {
        label: "Data One",
        backgroundColor: "rgb(228,102,81,0.9)",
        data: [30, 39, 20, 70, 30, 70, 35],
      },
    ]);
  };

  const getData = (type) => {
    //sent type get data from api
    Axios.get("http://api", { type }).then((res) => {
      setdataChart(res.data);
    });

    setdataChart([
      {
        label: "Data One",
        backgroundColor: "rgb(228,102,81,0.9)",
        data: [30, 39, 10, 50, 30, 70, 35],
      },
      {
        label: "Data Two",
        backgroundColor: "rgb(0,216,255,0.9)",
        data: [39, 80, 40, 35, 40, 20, 45],
      },
      {
        label: "Data Three",
        backgroundColor: "rgb(0,216,255,0.9)",
        data: [50, 60, 20, 45, 45, 30, 80],
      },
    ]);
  };

  useEffect(() => {
    //default get day
    getData(typeChart);
  }, []);

  return (
    <div>
      <NavbarPage
        SetshowDropdown={SetshowDropdown}
        showDropdown={showDropdown}
      />
      <CCard>
        <CCardBody>
          <CRow>
            <CCol sm="5">
              <h4 id="traffic" className="card-title mb-0">
                Traffic
              </h4>
              <div className="small text-muted">November 2017</div>
            </CCol>
            <CCol sm="7" className="d-none d-md-block">
              <CButtonGroup className="float-right mr-3">
                {CategoriesChart.map((value) => (
                  <button
                    color="outline-secondary"
                    key={value}
                    className="admin-button-CategoriesChart"
                    width="100%"
                    active={value === "Month"}
                    onClick={(e) => onChangeTypeChart(value)}
                  >
                    {value}
                  </button>
                ))}
                <CSelect className="admin-select-chart">
                  <option selected value="Day">วัน</option>
                  <option value="Month">เดือน</option>
                  <option value="Year">ปี</option>
                </CSelect>
              </CButtonGroup>
            </CCol>
          </CRow>
          {typeChart === "Day" ? (
            <CChartLine
              datasets={dataChart}
              options={{
                tooltips: {
                  enabled: true,
                },
              }}
              labels="months"
            />
          ) : null}
          {typeChart === "Month" ? (
            <CChartLine
              datasets={dataChart}
              options={{
                tooltips: {
                  enabled: true,
                },
              }}
              labels="months"
            />
          ) : null}
        </CCardBody>
        <CCardFooter>
          <CRow className="text-center">
            <CCol md sm="12" className="mb-sm-2 mb-0">
              <div className="text-muted">Visits</div>
              <strong>29.703 Users (40%)</strong>
              <CProgress
                className="progress-xs mt-2"
                precision={1}
                color="success"
                value={40}
              />
            </CCol>
            <CCol md sm="12" className="mb-sm-2 mb-0 d-md-down-none">
              <div className="text-muted">Unique</div>
              <strong>24.093 Users (20%)</strong>
              <CProgress
                className="progress-xs mt-2"
                precision={1}
                color="info"
                value={40}
              />
            </CCol>
            <CCol md sm="12" className="mb-sm-2 mb-0">
              <div className="text-muted">Pageviews</div>
              <strong>78.706 Views (60%)</strong>
              <CProgress
                className="progress-xs mt-2"
                precision={1}
                color="warning"
                value={40}
              />
            </CCol>
            <CCol md sm="12" className="mb-sm-2 mb-0">
              <div className="text-muted">New Users</div>
              <strong>22.123 Users (80%)</strong>
              <CProgress
                className="progress-xs mt-2"
                precision={1}
                color="danger"
                value={40}
              />
            </CCol>
            <CCol md sm="12" className="mb-sm-2 mb-0 d-md-down-none">
              <div className="text-muted">Bounce Rate</div>
              <strong>Average Rate (40.15%)</strong>
              <CProgress
                className="progress-xs mt-2"
                precision={1}
                value={40}
              />
            </CCol>
          </CRow>
        </CCardFooter>
      </CCard>
    </div>
  );
};

export default Dashboard;
