import React, { useEffect, useState } from "react";
import "./ranking.css";
import Chatbot from "../components/chatbot";
import NavbarPage from "../components/navnew";
import { MDBContainer, MDBRow, MDBCol } from "mdbreact";
import { MDBDataTable } from "mdbreact";
import { useAccordionToggle } from "react-bootstrap";
import Axios from "axios";
const Rank = () => {
  const [thiefData, setThiefData] = useState();
  const [dataRow, setDataRow] = useState();
  const getAPI = async () => {
    const getThief = await Axios.get(`http://localhost:7000/thief/rankcount`);
    setThiefData(getThief.data);
  };
  const setRow = () => {
    setDataRow(ThiefLoading());
  };
  useEffect(() => {
    getAPI();
    setRow();
  }, []);
  const num = [3, 8, 11, 7, 5];

const num2x = num.map((n) => n * 2);
  const ThiefLoading = () => {
  
    let ThiefData = thiefData.map((elements, index) => {
      return ( {
        rank: index,
        name: elements.name,
        lastname: elements.surname,
        account: elements.accountnumber,
        amount: elements.summoney,
        time: elements.count,
        date: elements.wanteedon,
      })
      
    });
    return ThiefData;
  }
  const data = {
    columns: [
      {
        label: "อันดับ",
        field: "rank",
        sort: "asc",
        width: 100,
      },
      {
        label: "ชื่อ",
        field: "name",
        sort: "asc",
        width: 200,
      },
      {
        label: "นามสกุล",
        field: "lastname",
        sort: "asc",
        width: 200,
      },
      {
        label: "เลขบัญชี",
        field: "account",
        sort: "asc",
        width: 100,
      },
      {
        label: "ยอดเงินทั้งหมด",
        field: "amount",
        sort: "asc",
        width: 150,
      },
      {
        label: "จำนวนครั้งที่โกง",
        field: "time",
        sort: "asc",
        width: 150,
      },
      {
        label: "วันที่โกง",
        field: "date",
        sort: "asc",
        width: 150,
      },
    ],
    rows: dataRow,
  };

  return (
    <div>
      <NavbarPage />
      <h1 className="h1-ranking">จัดอันดับคนโกง</h1>
      <MDBContainer className="container-ranking">
        <MDBDataTable
          responsive
          striped
          bordered
          paging={false}
          searching={false}
          data={data}
          className="rank-data"
        />
      </MDBContainer>
      <Chatbot />
    </div>
  );
};
export default Rank;
