import React, { useEffect, useState , useContext  } from "react";
import NavbarPage from "../../user/components/navnew";
import listverifypost from "../components/list_verify_port";
import Axios from "axios";
const Verifypost = () => {

  const [report, setReport] = useState();
  const [showDropdown, SetshowDropdown] = useState(true);
  const Hiddendropdown = () => {
    SetshowDropdown(false);
  };
  const initReport = async () => {
    try {
       const Allreport = Axios.get("http://localhost:7000/post/report/verify");
       setReport(Allreport.data.report);
    } catch (err) {
      console.log("error");
    }
  };

  useEffect(() => {
    initReport();
  }, []);


  return (
    <div onClick={() => Hiddendropdown()}>
      <NavbarPage
        SetshowDropdown={SetshowDropdown}
        showDropdown={showDropdown}
      />
      <h1 className="h1-history">ประวัติการโพสต์</h1>
      <div className="container-history5">
        {report ? (
          <h2 className="h2-history">ทั้งหมด {report.length} รายงาน</h2>
        ) : null}
      </div>
      {report
        ? report.map((reportelement, index) => {
            return (
              <listverifypost
                reportelement={reportelement}
                key={index}
              />
            );
          })
        : null}
    </div>
  );
};

export default Verifypost;
